# coding: utf-8import codecsf1 = codecs.open('data1.dat', 'r', 'ascii')  # data1.dat を入力のためにopenf2 = codecs.open('data2.dat', 'w', 'ascii')  # data2.dat を出力のためにopenfor kk in f1: # 1行ごとに読み込まれる    ll = kk.rsplit(' ')  # 空白を区切りとしてリストに分割    k1, k2 = float(ll[0]), float(ll[1])  # リストの各要素を実数に変換    f2.write(str(k1**2) + ' ' + str(k2**2) + '\n')  # 2乗値と空白，改行コードを書き込みf1.close()  # data1.dat をクローズf2.close()  # data2.dat をクローズ